head.script:


function Form_Load()
{
    if(window.dialogArguments["EventName"] != null)
    valueEventName.value = window.dialogArguments["EventName"];
}

function Form_OK()
{
    if(!valueEventName.value.length)
    {
        alert("You must enter a value for the event name.");
        return false;
    }
    
    return true;
}

function Form_Cancel()
{
    return true;
}
